    private void comenzarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comenzarActionPerformed
        n = Integer.parseInt(dato.getText().trim());
        impresion.setEnabled(true);
        dato.setText("");
        texto.setText("Digite el dato");


    }//GEN-LAST:event_comenzarActionPerformed

    private void okActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okActionPerformed

        if (i < n) {
            if (j < n) {
                mat[i][j] = Integer.parseInt(dato.getText());
                dato.setText("");
                texto.setText("Digite dato");
                j++;
            }
            if (j == n && i < n) {
                i++;
                j = 0;
            }
        }
        if (i == n) {
            texto.setText("DATOS COMPLETOS");
        }

    }//GEN-LAST:event_okActionPerformed

    private void imprimirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_imprimirActionPerformed
        // TODO add your handling code here:
        for (int i = 0; i < n; i++) {

            for (int j = 0; j < n; j++) {
                impresion.setText(impresion.getText() + String.valueOf(mat[i][j]) + " ");
            }
            impresion.setText(impresion.getText()+"\n ");
        }

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                System.out.print(mat[i][j] + "");

            }
            System.out.println("");
        }
    }//GEN-LAST:event_imprimirActionPerformed

